import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <nav className="relative w-full bg-white shadow-md px-8 py-6 flex flex-col items-center">
      {/* Web App Title - Centered */}
      <h1 className="text-3xl font-bold text-green-700 tracking-wide">
        Pet Resource Finder üêæ
      </h1>

      {/* Desktop Navigation */}
      <div className="hidden md:flex space-x-6 mt-3">
        <Link to="/" className="nav-link">Home</Link>
        <Link to="/search" className="nav-link">Search</Link>
        <Link to="/about" className="nav-link">About</Link>
      </div>
    </nav>
  );
};

// ‚úÖ Styling
const styles = `
  .nav-link {
    font-size: 1rem;
    font-weight: 500;
    color: #4A5568;
    padding: 0.5rem 1rem;
    transition: color 0.3s ease, transform 0.2s ease;
  }
  .nav-link:hover {
    color: #2F855A;
    transform: translateY(-2px);
  }
`;

export default () => (
  <>
    <style>{styles}</style>
    <Navbar />
  </>
);
