export const getUserId = () => {
    let userId = localStorage.getItem("tempUserId");
    if (!userId) {
      userId = crypto.randomUUID();// ✅ Generate a unique ID
      localStorage.setItem("tempUserId", userId);
    }
    return userId;
};
