import React, { useState } from "react";
import {
  LoadScript,
  GoogleMap,
  Autocomplete,
  DirectionsRenderer,
  Marker,
} from "@react-google-maps/api";

// Components
import MapCentering from "./MapCentering";
import MarkersWithInfo from "./MarkersWithInfo";
import ResultsList from "./ResultsList";
import RecentSearches from "./RecentSearches";
import DirectionsPanel from "./DirectionsPanel";

// useHooks
import useMap from "../hooks/useMap";
import useMapControl from "../hooks/useMapControls";
import useSearch from "../hooks/useSearch";
import useDirections from "../hooks/useDirections";
import useUserLocation from "../hooks/useUserLocationStorage";

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
const libraries = ["places"];

const MapWithAutocomplete = () => {
  const {
    map,
    setMap,
    center,
    setCenter,
    searchBox,
    setSearchBox,
    mapSize,
    handlePlaceChanged,
    selectedPlace,
    setSelectedPlace,
  } = useMap();

  const {
    type,
    setType,
    markers,
    setMarkers,
    allMarkers,
    setAllMarkers,
    hoveredIndex,
    setHoveredIndex,
  } = useMapControl();

  const {
    lastSearch,
    setLastSearch,
    recentSearches,
    setRecentSearches,
  } = useUserLocation();

  const {
    fetchPlaces,
    handleRefetchSearch,
    deleteRecentSearch,
  } = useSearch(setMarkers, setAllMarkers, type, setLastSearch, setRecentSearches);

  const {
    directionsRaw,
    directionsParsed,
    setDirectionsParsed,
    stepMarkers,
    setStepMarkers,
    hoveredStep,
    setHoveredStep,
    clearDirections,
    setDirectionsRaw,
    getDirections,
  } = useDirections(setMarkers, allMarkers, setRecentSearches);

  const [activeStepIndex, setActiveStepIndex] = useState(null);
  const [activeResultIndex, setActiveResultIndex] = useState(null);

  const handleResultHover = (index) => {
    setHoveredIndex(index);
  };

  const handleStepClick = (index) => {
    const step = stepMarkers[index];
    if (step) {
      setCenter({ lat: step.lat, lng: step.lng });
      setActiveStepIndex(index);
    }
  };

  const handleInstructionMarkerClick = (index) => {
    setActiveStepIndex(index);
  };

  const handleClearRoute = () => {
    clearDirections(() => {
      setMarkers(allMarkers);
    });
    setActiveStepIndex(null);
  };

  return (
    <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY} libraries={libraries}>
      <div className="flex flex-col h-screen w-screen bg-gradient-to-br from-green-300 via-yellow-300 to-orange-400 p-8">
        <div className="flex flex-grow justify-center items-start gap-6">
          {/* Sidebar */}
          <div className="w-[48%] h-[75vh] bg-white p-6 rounded-2xl shadow-lg flex flex-col relative">
            <RecentSearches
              searches={recentSearches}
              onRefetch={handleRefetchSearch}
              onDelete={deleteRecentSearch}
              onRevisit={(search) => {
                const origin = search.location?.string?.from;
                const destination = search.location?.string?.to;

                if (!origin || !destination || origin === "Unknown" || destination === "Unknown") return;

                // Reuse getDirections logic
                getDirections(destination, origin); // âœ… Thread in full search logic
              }}
            />
            <div className="border-t border-gray-300 my-4"></div>

            <ResultsList
              markers={markers}
              setSelectedPlace={setSelectedPlace}
              setCenter={setCenter}
              setDirectionsRaw={setDirectionsRaw}
              setDirectionsParsed={setDirectionsParsed}
              clearParsedDirections={handleClearRoute}
              lastSearch={lastSearch}
              type={type}
              setRecentSearches={setRecentSearches}
              setStepMarkers={setStepMarkers}
              onStepHover={(stepIndex) => {
                const step = stepMarkers[stepIndex];
                if (step) {
                  setHoveredStep({ lat: step.lat, lng: step.lng });
                }
              }}
              onResultHover={handleResultHover}
              hoveredIndex={hoveredIndex}
              activeStepIndex={activeStepIndex}
              onStepClick={handleStepClick}
              activeResultIndex={activeResultIndex}
              setActiveResultIndex={setActiveResultIndex}
            />

            {directionsParsed && (
              <div className="absolute inset-0 bg-white bg-opacity-95 p-4 rounded-xl shadow-lg overflow-y-auto z-20">
                <DirectionsPanel
                  directions={directionsParsed}
                  onClose={handleClearRoute}
                  onStepHover={(stepIndex) => {
                    const step = stepMarkers[stepIndex];
                    if (step) {
                      setHoveredStep({ lat: step.lat, lng: step.lng });
                    }
                  }}
                  activeStepIndex={activeStepIndex}
                  onStepClick={handleStepClick}
                />
              </div>
            )}
          </div>

          {/* Map */}
          <div className="w-[48%] h-[75vh] bg-white p-6 rounded-2xl shadow-lg flex flex-col relative">
            <Autocomplete
              onLoad={setSearchBox}
              onPlaceChanged={() => {
                if (searchBox) {
                  handlePlaceChanged(searchBox, fetchPlaces, setLastSearch);
                }
              }}
            >
              <input
                type="text"
                placeholder="Enter a location"
                className="w-full p-3 rounded-lg border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-300"
              />
            </Autocomplete>

            <select
              value={type}
              onChange={(e) => setType(e.target.value)}
              className="mt-3 p-3 rounded-lg bg-white border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              <option value="veterinary_care">Veterinary Care</option>
              <option value="pet_store">Pet Store</option>
              <option value="dog_park">Dog Park</option>
            </select>

            <div className="flex-grow rounded-xl overflow-hidden shadow-lg border border-gray-300 mt-4 relative">
              <GoogleMap
                center={center}
                zoom={12}
                mapContainerStyle={mapSize}
                onLoad={setMap}
              >
                <MapCentering center={center} />
                {!directionsRaw && (
                  <MarkersWithInfo
                    markers={markers}
                    selectedPlace={selectedPlace}
                    setSelectedPlace={setSelectedPlace}
                    setCenter={setCenter}
                    map={map}
                    hoveredIndex={hoveredIndex}
                    activeResultIndex={activeResultIndex}
                    setActiveResultIndex={setActiveResultIndex}
                  />
                )}

                {stepMarkers.map((step, index) => (
                  <Marker
                    key={`step-${index}`}
                    position={{ lat: step.lat, lng: step.lng }}
                    label={(index + 1).toString()}
                    onClick={() => setActiveStepIndex(index)}
                  />
                ))}

                {directionsRaw && <DirectionsRenderer directions={directionsRaw} />}
              </GoogleMap>
            </div>
          </div>
        </div>
      </div>
    </LoadScript>
  );
};

export default MapWithAutocomplete;
