import React, { useState, useEffect } from "react";
import {
  LoadScript,
  GoogleMap,
  Autocomplete,
  DirectionsRenderer,
  Marker,
  MarkerClusterer,
  InfoWindow,
} from "@react-google-maps/api";

import MapCentering from "./MapCentering";
import ResultsList from "./ResultsList";
import RecentSearches from "./RecentSearches";
import CategoryTabs from "./CategoryTabs";

import useMap from "../hooks/useMap";
import useMapControl from "../hooks/useMapControls";
import useSearch from "../hooks/useSearch";
import useDirections from "../hooks/useDirections";
import useUserLocation from "../hooks/useUserLocationStorage";

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
const libraries = ["places"];
const CLUSTER_ZOOM_THRESHOLD = 15;

const MapWithAutocomplete = () => {
  const {
    map,
    setMap,
    center,
    setCenter,
    searchBox,
    setSearchBox,
    selectedPlace,
    setSelectedPlace,
  } = useMap();

  const {
    type,
    setType,
    markers,
    setMarkers,
    allMarkers,
    setAllMarkers,
    hoveredIndex,
    setHoveredIndex,
  } = useMapControl();

  const {
    lastSearch,
    setLastSearch,
    recentSearches,
    setRecentSearches,
  } = useUserLocation();

  const {
    fetchPlaces,
    handleRefetchSearch,
    handlePlaceChanged,
    deleteRecentSearch,
    refreshRecentSearches,
    loading,
  } = useSearch(
    setMarkers,
    setAllMarkers,
    type,
    setLastSearch,
    setRecentSearches,
    setCenter
  );

  const {
    directionsRaw,
    directionsParsed,
    stepMarkers,
    clearDirections,
    getDirections,
  } = useDirections(setMarkers, allMarkers, setRecentSearches);

  const [activeStepIndex, setActiveStepIndex] = useState(null);
  const [activeResultIndex, setActiveResultIndex] = useState(null);
  const [userPosition, setUserPosition] = useState(null);
  const [zoom, setZoom] = useState(12);

  // Only show markers with ratings
  const ratedMarkers = markers.filter((place) => place.rating != null);

  const handleMapLoad = (mapInstance) => {
    setMap(mapInstance);
    setZoom(mapInstance.getZoom());
    mapInstance.addListener("zoom_changed", () => setZoom(mapInstance.getZoom()));
  };

  useEffect(() => {
    if (!map) return;
    navigator.geolocation.getCurrentPosition(({ coords }) => {
      const pos = { lat: coords.latitude, lng: coords.longitude };
      setUserPosition(pos);
      map.panTo(pos);
    });
  }, [map]);

  const handleResultHover = (index) => setHoveredIndex(index);

  const handleStepClick = (index) => {
    const step = stepMarkers[index];
    if (step) {
      const loc = { lat: step.lat, lng: step.lng };
      setCenter(loc);
      map.panTo(loc);
      map.setZoom(15);
      setActiveStepIndex(index);
    }
  };

  const handleClearRoute = () => {
    clearDirections(() => setMarkers(allMarkers));
    setActiveStepIndex(null);
    setActiveResultIndex(null);
  };

  return (
    <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY} libraries={libraries}>
      <div className="flex flex-col h-screen w-screen bg-gradient-to-br from-green-300 via-yellow-300 to-orange-400 p-8">
        <div className="flex flex-grow justify-center items-start gap-6">

          {/* Left Panel */}
          <div className="w-[48%] h-[75vh] bg-white p-6 rounded-2xl shadow-lg flex flex-col relative">
            <RecentSearches
              searches={recentSearches}
              onRefetch={refreshRecentSearches}
              onDelete={deleteRecentSearch}
              onRevisit={(orig, dest) => getDirections(orig, dest, type)}
            />

            <div className="border-t border-gray-300 my-4" />

            <ResultsList
              markers={ratedMarkers}
              loading={loading}
              lastSearch={lastSearch}
              onGetDirections={(orig, dest) => getDirections(orig, dest, type)}
              onResultHover={handleResultHover}
              hoveredIndex={hoveredIndex}
              activeResultIndex={activeResultIndex}
              setActiveResultIndex={setActiveResultIndex}
              directionsParsed={directionsParsed}
              onStepHover={handleStepClick}
              activeStepIndex={activeStepIndex}
              onStepClick={handleStepClick}
              clearParsedDirections={handleClearRoute}
              setCenter={setCenter}
              setSelectedPlace={setSelectedPlace}
            />
          </div>

          {/* Right Panel */}
          <div className="w-[48%] h-[75vh] bg-white p-6 rounded-2xl shadow-lg flex flex-col relative">
            <Autocomplete
              onLoad={setSearchBox}
              onPlaceChanged={() => handlePlaceChanged(searchBox)}
            >
              <input
                type="text"
                placeholder="Enter a location"
                className="w-full p-3 rounded-lg border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-300"
              />
            </Autocomplete>

            <div className="flex justify-center mt-3">
              <CategoryTabs
                categories={[
                  { label: "Veterinary Care", value: "veterinary_care" },
                  { label: "Pet Store", value: "pet_store" },
                  { label: "Dog Park", value: "dog_park" },
                ]}
                selectedCategory={type}
                onSelect={setType}
              />
            </div>

            <div className="flex-grow rounded-xl overflow-hidden shadow-lg border border-gray-300 mt-4 relative focus-within:ring-4 focus-within:ring-green-200 outline-none">
              <GoogleMap
                center={center}
                zoom={zoom}
                mapContainerStyle={{ width: '100%', height: '100%' }}
                onLoad={handleMapLoad}
              >
                <MapCentering center={center} />

                {/* Cluster markers when zoom is below threshold */}
                {!directionsRaw && zoom < CLUSTER_ZOOM_THRESHOLD && (
                  <MarkerClusterer options={{ minimumClusterSize: 2 }}>
                    {(clusterer) =>
                      ratedMarkers.map((place, idx) => (
                        <Marker
                          key={place.place_id || idx}
                          position={place.geometry.location}
                          clusterer={clusterer}
                          label={`${idx + 1}`}
                          onMouseOver={() => setHoveredIndex(idx)}
                          onMouseOut={() => setHoveredIndex(null)}
                          onClick={() => {
                            setSelectedPlace(place);
                            setCenter(place.geometry.location);
                            setActiveResultIndex(idx);
                          }}
                        />
                      ))
                    }
                  </MarkerClusterer>
                )}

                {/* Individual red markers when zoom is at or above threshold */}
                {!directionsRaw && zoom >= CLUSTER_ZOOM_THRESHOLD &&
                  ratedMarkers.map((place, idx) => (
                    <Marker
                      key={place.place_id || idx}
                      position={place.geometry.location}
                      label={`${idx + 1}`}
                      onMouseOver={() => setHoveredIndex(idx)}
                      onMouseOut={() => setHoveredIndex(null)}
                      onClick={() => {
                        setSelectedPlace(place);
                        setCenter(place.geometry.location);
                        setActiveResultIndex(idx);
                      }}
                    />
                  ))
                }

                {/* InfoWindow for selected place */}
                {selectedPlace && selectedPlace.geometry?.location && (
                  <InfoWindow
                    position={selectedPlace.geometry.location}
                    onCloseClick={() => setSelectedPlace(null)}
                  >
                    <div className="p-2">
                      <strong>{selectedPlace.name}</strong>
                      {selectedPlace.formatted_phone_number && (
                        <p className="text-sm text-gray-600 mt-1">
                          <strong>Phone:</strong> {selectedPlace.formatted_phone_number}
                        </p>
                      )}
                      {selectedPlace.rating && (
                        <p className="text-sm text-yellow-600 mt-1">
                          <strong>Rating:</strong> {selectedPlace.rating} ‚≠ê
                        </p>
                      )}
                      {selectedPlace.vicinity && (
                        <p className="text-sm text-gray-600">{selectedPlace.vicinity}</p>
                      )}
                    </div>
                  </InfoWindow>
                )}

                {/* Routing step markers */}
                {stepMarkers.map((step, idx) => (
                  <Marker
                    key={`step-${idx}`}
                    position={{ lat: step.lat, lng: step.lng }}
                    label={`${idx + 1}`}
                    onClick={() => handleStepClick(idx)}
                  />
                ))}

                {directionsRaw && (
                  <DirectionsRenderer directions={directionsRaw} options={{ suppressMarkers: true }} />
                )}
              </GoogleMap>
            </div>
          </div>
        </div>
      </div>
    </LoadScript>
  );
};

export default MapWithAutocomplete;
