import React from "react";
import { motion } from "framer-motion";

const DirectionsPanel = ({
  steps = [],
  isOpen,
  onClose,
  onStepHover,
  activeStepIndex,
  onStepClick,
}) => {
  return (
    <motion.div
      className="fixed top-0 right-0 w-80 h-full bg-white shadow-2xl z-50 p-6 rounded-l-2xl overflow-y-auto focus:outline-none focus:ring-4 focus:ring-green-200"
      initial={{ x: "100%" }}
      animate={{ x: isOpen ? 0 : "100%" }}
      exit={{ x: "100%" }}
      transition={{ type: "spring", stiffness: 300, damping: 30 }}
    >
      <button
        onClick={onClose}
        className="mb-4 px-3 py-1 rounded bg-gray-200 hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-green-300"
      >
        Close
      </button>
      <ol className="space-y-2">
        {steps.map((item, idx) => {
          // Determine display text: if item is string, use directly; if object, use property 'instructions' or 'text'
          const text =
            typeof item === 'string'
              ? item
              : item.instructions || item.text || JSON.stringify(item);
          return (
            <li
              key={idx}
              onMouseEnter={() => onStepHover(idx)}
              onClick={() => onStepClick(idx)}
              className={`cursor-pointer text-sm transition-colors ${
                activeStepIndex === idx
                  ? 'text-green-600 font-semibold'
                  : 'text-gray-700 hover:text-green-500'
              }`}
            >
              {text}
            </li>
          );
        })}
      </ol>
    </motion.div>
  );
};

export default DirectionsPanel;
