import React from 'react';

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

const MiniProfileCard = ({ place, onClose, onGetDirections, lastSearch, category }) => {
  const photoRef = place.photos?.[0]?.photo_reference;
  const photoUrl = photoRef
    ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=200&photoreference=${photoRef}&key=${GOOGLE_MAPS_API_KEY}`
    : null;

  return (
    <div className="absolute top-4 left-4 bg-white p-4 rounded-lg shadow-lg z-40 w-64">
      <button
        onClick={onClose}
        className="absolute top-2 right-2 text-red-500 hover:text-red-700"
        title="Close"
      >
        âœ•
      </button>
      {photoUrl && (
        <img
          src={photoUrl}
          alt={place.name}
          className="w-full h-24 object-cover rounded-md mb-2"
        />
      )}
      <h5 className="font-semibold text-gray-800 mb-1">{place.name}</h5>
      <p className="text-sm text-gray-600 mb-1">
        {place.vicinity || place.formatted_address}
      </p>
      {place.opening_hours?.weekday_text && (
        <ul className="text-xs text-gray-600 mb-2 space-y-1">
          {place.opening_hours.weekday_text.map((t, i) => (
            <li key={i}>{t}</li>
          ))}
        </ul>
      )}
      <button
        onClick={() => onGetDirections(lastSearch, place.vicinity, category)}
        className="mt-2 w-full bg-green-500 text-white py-1 rounded hover:bg-green-600"
      >
        Get Directions
      </button>
    </div>
  );
};

export default MiniProfileCard;
