import React, { useState, useEffect } from "react";
import {
  LoadScript,
  GoogleMap,
  Autocomplete,
  DirectionsRenderer,
  Marker,
  MarkerClusterer,
  OverlayView,
} from "@react-google-maps/api";

import MapCentering from "./MapCentering";
import ResultsList from "./ResultsList";
import RecentSearches from "./RecentSearches";

import useMap from "../hooks/useMap";
import useMapControl from "../hooks/useMapControls";
import useSearch from "../hooks/useSearch";
import useDirections from "../hooks/useDirections";
import useUserLocation from "../hooks/useUserLocationStorage";

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
const libraries = ["places"];
const CLUSTER_ZOOM_THRESHOLD = 15;

const MapWithAutocomplete = () => {
  const { map, setMap, center, setCenter, searchBox, setSearchBox, selectedPlace, setSelectedPlace } = useMap();
  const { type, setType, markers, setMarkers, allMarkers, setAllMarkers, hoveredIndex, setHoveredIndex } = useMapControl();
  const { lastSearch, setLastSearch, recentSearches, setRecentSearches } = useUserLocation();
  const { fetchPlaces, handleRefetchSearch, handlePlaceChanged, deleteRecentSearch, refreshRecentSearches, loading } = useSearch(
    setMarkers,
    setAllMarkers,
    type,
    setLastSearch,
    setRecentSearches,
    setCenter
  );
  const {
    directionsRaw,
    directionsParsed,
    setDirectionsParsed,
    stepMarkers,
    setStepMarkers,
    hoveredStep,
    setHoveredStep,
    clearDirections,
    setDirectionsRaw,
    getDirections,
  } = useDirections(setMarkers, allMarkers, setRecentSearches);

  const [activeStepIndex, setActiveStepIndex] = useState(null);
  const [activeResultIndex, setActiveResultIndex] = useState(null);
  const [userPosition, setUserPosition] = useState(null);
  const [zoom, setZoom] = useState(12);

  // handle map load and track zoom
  const handleMapLoad = (mapInstance) => {
    setMap(mapInstance);
    setZoom(mapInstance.getZoom());
    mapInstance.addListener("zoom_changed", () => {
      setZoom(mapInstance.getZoom());
    });
  };

  // get user location once map is ready
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(({ coords }) => {
      const pos = { lat: coords.latitude, lng: coords.longitude };
      setUserPosition(pos);
      map?.panTo(pos);
    });
  }, [map]);

  const handleResultHover = (index) => setHoveredIndex(index);

  const handleStepClick = (index) => {
    const step = stepMarkers[index];
    if (step) {
      setCenter({ lat: step.lat, lng: step.lng });
      setActiveStepIndex(index);
    }
  };

  const handleClearRoute = () => {
    clearDirections(() => {
      setMarkers(allMarkers);
    });
    setActiveStepIndex(null);
    setActiveResultIndex(null);
  };

  return (
    <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY} libraries={libraries}>
      <div className="flex flex-col h-screen w-screen bg-gradient-to-br from-green-300 via-yellow-300 to-orange-400 p-8">
        <div className="flex flex-grow justify-center items-start gap-6">
          {/* Left Panel */}
          <div className="w-[48%] h-[75vh] bg-white p-6 rounded-2xl shadow-lg flex flex-col relative">
            <RecentSearches
              searches={recentSearches}
              onRefetch={refreshRecentSearches}
              onDelete={deleteRecentSearch}
              onRevisit={(origin, destination) => {
                getDirections(origin, destination).then((result) => {
                  if (result) {
                    setDirectionsRaw(result.raw);
                    setDirectionsParsed(result.parsed);
                    setStepMarkers(result.steps);
                  }
                });
              }}
            />
            <div className="border-t border-gray-300 my-4" />
            <ResultsList
              markers={markers}
              loading={loading}
              setSelectedPlace={setSelectedPlace}
              setCenter={setCenter}
              setDirectionsRaw={setDirectionsRaw}
              setDirectionsParsed={setDirectionsParsed}
              clearParsedDirections={handleClearRoute}
              lastSearch={lastSearch}
              type={type}
              setRecentSearches={setRecentSearches}
              setStepMarkers={setStepMarkers}
              onStepHover={(i) => {
                const s = stepMarkers[i];
                if (s) setHoveredStep({ lat: s.lat, lng: s.lng });
              }}
              onGetDirections={(origin, dest) => getDirections(origin, dest, type)}
              onResultHover={handleResultHover}
              hoveredIndex={hoveredIndex}
              activeStepIndex={activeStepIndex}
              onStepClick={handleStepClick}
              activeResultIndex={activeResultIndex}
              setActiveResultIndex={setActiveResultIndex}
              directionsParsed={directionsParsed}
            />
          </div>
          {/* Right Panel */}
          <div className="w-[48%] h-[75vh] bg-white p-6 rounded-2xl shadow-lg flex flex-col relative">
            <Autocomplete onLoad={setSearchBox} onPlaceChanged={() => handlePlaceChanged(searchBox)}>
              <input
                type="text"
                placeholder="Enter a location"
                className="w-full p-3 rounded-lg border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 transition duration-300"
              />
            </Autocomplete>
            <select
              value={type}
              onChange={(e) => setType(e.target.value)}
              className="mt-3 p-3 rounded-lg bg-white border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 block mx-auto text-center"
            >
              <option value="veterinary_care">Veterinary Care</option>
              <option value="pet_store">Pet Store</option>
              <option value="dog_park">Dog Park</option>
            </select>
            <div
              tabIndex={0}
              className="flex-grow rounded-xl overflow-hidden shadow-lg border border-gray-300 mt-4 relative focus-within:ring-4 focus-within:ring-green-200 outline-none"
            >
              <GoogleMap
                center={center}
                zoom={zoom}
                mapContainerStyle={{ width: "100%", height: "100%" }}
                onLoad={handleMapLoad}
              >
                <MapCentering center={center} />
                {!directionsRaw && (
                  zoom < CLUSTER_ZOOM_THRESHOLD ? (
                    <MarkerClusterer>
                      {(clusterer) =>
                        markers.map((place, idx) => (
                          <Marker
                            key={place.place_id || idx}
                            position={place.geometry.location}
                            clusterer={clusterer}
                            onMouseOver={() => setHoveredIndex(idx)}
                            onMouseOut={() => setHoveredIndex(null)}
                            onClick={() => {
                              setSelectedPlace(place);
                              setCenter(place.geometry.location);
                              setActiveResultIndex(idx);
                            }}
                          />
                        ))
                      }
                    </MarkerClusterer>
                  ) : (
                    markers.map((place, idx) => (
                      <Marker
                        key={place.place_id || idx}
                        position={place.geometry.location}
                        onMouseOver={() => setHoveredIndex(idx)}
                        onMouseOut={() => setHoveredIndex(null)}
                        onClick={() => {
                          setSelectedPlace(place);
                          setCenter(place.geometry.location);
                          setActiveResultIndex(idx);
                        }}
                        icon={{ url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png" }}
                      />
                    ))
                  )
                )}
                {userPosition && (
                  <OverlayView
                    position={userPosition}
                    mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET}
                  >
                    <div className="relative">
                      <div className="absolute inset-0 w-6 h-6 bg-green-500 rounded-full animate-ping opacity-50" />
                      <div className="relative w-3 h-3 bg-green-600 rounded-full" />
                    </div>
                  </OverlayView>
                )}
                {stepMarkers.map((step, idx) => (
                  <Marker
                    key={`step-${idx}`}
                    position={{ lat: step.lat, lng: step.lng }}
                    label={`${idx + 1}`}
                    onClick={() => handleStepClick(idx)}
                  />
                ))}
                {directionsRaw && <DirectionsRenderer directions={directionsRaw} />}
              </GoogleMap>
            </div>
          </div>
        </div>
      </div>
    </LoadScript>
  );
};

export default MapWithAutocomplete;
