import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const RecentSearches = ({ searches, onRefetch, onDelete, onRevisit }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="w-full bg-white rounded-lg shadow-md p-4">
      {/* Toggle Header */}
      <div className="flex justify-between items-center">
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="flex-grow flex justify-between items-center p-3 bg-gray-100 hover:bg-gray-200 rounded-md text-gray-700 font-semibold transition-all duration-300"
        >
          Recent Searches
          <motion.span
            animate={{ rotate: isOpen ? 180 : 0 }}
            transition={{ duration: 0.3 }}
          >
            ⌄
          </motion.span>
        </button>

        {/* Refresh Button */}
        <button
          onClick={onRefetch}
          className="ml-2 p-2 rounded-md border border-blue-300 hover:bg-blue-100 transition-all duration-300 group"
          title="Refresh Recent Searches"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-5 w-5 text-blue-600 group-hover:animate-spin transition-transform duration-300"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            strokeWidth={2}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M4 4v5h.582m15.154 2A9.003 9.003 0 006.582 9m0 0A9.003 9.003 0 0118.418 15m0 0v5h-.582"
            />
          </svg>
        </button>
      </div>

      {/* Dropdown Content */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: "auto", opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            className="mt-3 overflow-hidden"
          >
            {searches.length > 0 ? (
              <ul className="space-y-3">
                {searches.map((search, index) => (
                  <motion.li
                    key={search.id || index}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: 20 }}
                    transition={{ duration: 0.3, delay: index * 0.05 }}
                    className="p-3 bg-gray-50 rounded-md shadow-sm hover:bg-gray-100 transition-all duration-300 relative cursor-pointer"
                    onClick={() => onRevisit?.(search)} // ✅ Only calls if onRevisit is passed
                  >
                    <strong className="block capitalize">
                      {search.searchType
                        .split("_")
                        .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
                        .join(" ")}
                    </strong>
                    <p className="text-gray-600 text-sm">
                      From: {search.location?.string?.from || "Unknown"}
                    </p>
                    <p className="text-gray-600 text-sm">
                      To: {search.location?.string?.to || "Unknown"}
                    </p>
                    {/* Delete Button */}
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onDelete(search.id)}}
                      className="absolute top-2 right-2 p-1 text-red-500 hover:text-red-700 transition-colors duration-300"
                      title="Delete this search"
                    >
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="h-6 w-6"
                        viewBox="0 0 20 20"
                        fill="currentColor"
                      >
                        <path
                          fillRule="evenodd"
                          d="M6.293 6.293a1 1 0 011.414 0L10 8.586l2.293-2.293a1 1 0 111.414 
                          1.414L11.414 10l2.293 2.293a1 1 0 01-1.414 
                          1.414L10 11.414l-2.293 2.293a1 1 0 01-1.414-1.414L8.586 
                          10 6.293 7.707a1 1 0 010-1.414z"
                          clipRule="evenodd"
                        />
                      </svg>
                    </button>
                  </motion.li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 text-sm mt-2">No recent searches.</p>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default RecentSearches;
