import React from "react";
import Skeleton from 'react-loading-skeleton';
import { motion, AnimatePresence } from "framer-motion";
import DirectionsPanel from '../components/DirectionsPanel';

const ResultsList = ({
  markers = [],
  loading = false,
  stepMarkers = [],
  setSelectedPlace,
  setCenter,
  clearParsedDirections,
  lastSearch,
  type,
  onStepHover,
  onGetDirections,
  onResultHover,
  hoveredIndex,
  activeStepIndex,
  onStepClick,
  activeResultIndex,
  setActiveResultIndex,
  directionsParsed,
}) => {
  // Show skeletons while loading
  if (loading) {
    return (
      <div className="w-full h-full p-6 bg-white rounded-2xl shadow-lg flex flex-col overflow-hidden relative">
        <h3 className="text-lg font-semibold text-gray-700 text-center mb-3">Search Results</h3>
        <div className="space-y-4 flex-grow p-4">
          {[...Array(5)].map((_, i) => (
            <div key={i} className="p-4 border rounded-lg bg-gray-200 animate-pulse">
              <div className="h-5 bg-gray-300 rounded w-3/5 mb-2" />
              <div className="h-4 bg-gray-300 rounded w-2/5" />
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="w-full h-full p-6 bg-white rounded-2xl shadow-lg flex flex-col overflow-hidden relative">
      <h3 className="text-lg font-semibold text-gray-700 text-center mb-3">Search Results</h3>

      <AnimatePresence mode="popLayout">
        <ul className="list-none space-y-3 flex-grow overflow-y-auto">
          {markers.length > 0 ? (
            markers.map((place, index) => (
              <motion.li
                key={place.place_id || index}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
                transition={{ duration: 0.3, delay: index * 0.03 }}
                className={`p-3 border-b border-gray-200 cursor-pointer flex justify-between items-center rounded-lg transition-all duration-300 ${
                  hoveredIndex === index || activeResultIndex === index
                    ? "bg-green-100 text-green-700 font-semibold"
                    : "hover:bg-gray-100 text-gray-700"
                }`}
                onClick={() => {
                  setSelectedPlace(place);
                  setCenter(place.geometry.location);
                  setActiveResultIndex(index);
                }}
                onMouseEnter={() => onResultHover(index)}
                onMouseLeave={() => onResultHover(null)}
              >
                <div className="w-full">
                  <strong>{index + 1}. {place.name}</strong>
                  {place.rating && <p className="text-sm text-yellow-600 mt-1"><strong>Rating:</strong> {place.rating} ‚≠ê</p>}
                  {place.formatted_phone_number && <p className="text-sm text-gray-600 mt-1"><strong>Phone:</strong> {place.formatted_phone_number}</p>}
                  <p className="text-sm text-gray-600">{place.vicinity}</p>
                </div>
                <button
                  className="text-green-500 text-lg hover:text-green-700 transition ml-4"
                  onClick={(e) => {
                    e.stopPropagation();
                    onGetDirections(lastSearch, place.vicinity);
                  }}
                >üìç</button>
              </motion.li>
            ))
          ) : (
            <p className="text-center text-gray-500 mt-4">No search results found.</p>
          )}
        </ul>
      </AnimatePresence>

      {directionsParsed && (
        <div className="absolute inset-0 bg-white bg-opacity-95 p-4 rounded-xl shadow-lg overflow-y-auto z-20">
          <DirectionsPanel
            steps={directionsParsed.legs[0].steps}
            isOpen={true}
            onClose={clearParsedDirections}
            onStepHover={onStepHover}
            activeStepIndex={activeStepIndex}
            onStepClick={onStepClick}
          />
        </div>
      )}
    </div>
  );
};

export default ResultsList;
