import React from "react";

const DirectionsPanel = ({ directions, onClose, onStepHover, activeStepIndex, onStepClick }) => {
  if (!directions || !directions.legs || directions.legs.length === 0) return null;

  return (
    <div className="w-full h-full bg-white p-6 rounded-2xl shadow-lg overflow-y-auto flex flex-col border border-gray-300 z-50">
      {/* Header */}
      <div className="flex justify-between items-center mb-4 border-b border-gray-300 pb-2">
        <h3 className="text-lg font-semibold text-gray-700">Route Instructions</h3>
        <button
          onClick={onClose}
          className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition"
        >
          Cancel Route
        </button>
      </div>

      {/* Directions Body */}
      <div className="space-y-6">
        {directions.legs.map((leg, legIndex) => (
          <div key={legIndex}>
            <p className="text-sm font-medium text-gray-800">
              {leg.startAddress} → {leg.endAddress}
            </p>
            <p className="text-sm text-gray-600 mb-2">
              {leg.distance} ({leg.duration})
            </p>

            <ol className="list-decimal pl-6 space-y-2">
              {leg.steps.map((step, stepIndex) => (
                <li
                key={`${legIndex}-${stepIndex}`}
                onClick={() => onStepClick(stepIndex)} // ✅ Trigger it here
                onMouseEnter={() => onStepHover(stepIndex)}
                onMouseLeave={() => onStepHover(null)}
                className={`text-sm text-gray-700 hover:text-green-600 cursor-pointer transition-colors ${
                  activeStepIndex === stepIndex ? "bg-green-100 font-semibold" : ""
                }`}
              >
                {step}
              </li>
              ))}
            </ol>
          </div>
        ))}
      </div>

      {/* Footer Separator */}
      <div className="border-t border-gray-300 mt-6 pt-4 w-full" />
    </div>
  );
};

export default DirectionsPanel;
