import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { formatSearchType } from "../utils/searchHandler"; // âœ… formatting helper

const RecentSearches = ({ searches, onRefetch, onDelete }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="w-full bg-white rounded-lg shadow-md p-4">
      {/* Toggle Header */}
      <div className="flex justify-between items-center">
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="flex-grow flex justify-between items-center p-3 bg-gray-100 hover:bg-gray-200 rounded-md text-gray-700 font-semibold transition-all duration-300"
        >
          Recent Searches
          <motion.span
            animate={{ rotate: isOpen ? 180 : 0 }}
            transition={{ duration: 0.3 }}
          >
            âŒ„
          </motion.span>
        </button>

        {/* Refetch Button */}
        <button
          onClick={onRefetch}
          className="ml-2 px-3 py-2 text-sm text-blue-600 border border-blue-300 rounded-md hover:bg-blue-100 transition-all"
        >
          ðŸ”„ Refresh
        </button>
      </div>

      {/* Dropdown Content */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: "auto", opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            className="mt-3 overflow-hidden"
          >
            {searches.length > 0 ? (
              <ul className="space-y-3">
                {searches.map((search, index) => (
                  <motion.li
                    key={index}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: 20 }}
                    transition={{ duration: 0.3, delay: index * 0.05 }}
                    className="p-3 bg-gray-50 rounded-md shadow-sm hover:bg-gray-100 transition-all duration-300 flex justify-between items-start"
                  >
                    <div className="flex-1 cursor-pointer" onClick={() => onRefetch(search.searchType, { coords: search.location.origin })}>
                      <strong className="block capitalize">{formatSearchType(search.searchType)}</strong>
                      <p className="text-gray-600 text-sm">
                        From: {search.location?.string?.from || "Unknown"}
                      </p>
                      <p className="text-gray-600 text-sm">
                        To: {search.location?.string?.to || "Unknown"}
                      </p>
                    </div>

                    <button
                      onClick={() => onDelete(search.id)}
                      className="ml-3 text-red-500 hover:text-red-700 font-bold text-lg"
                    >
                      âœ•
                    </button>
                  </motion.li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 text-sm mt-2">No recent searches.</p>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default RecentSearches;
