import React from "react";
import { motion, AnimatePresence } from "framer-motion";
import { saveSearchQuery, fetchRecentSearches } from "../utils/searchHandler";
import DirectionsPanel from '../components/DirectionsPanel';
import axios from "axios";

const ResultsList = ({
  markers = [],
  setSelectedPlace,
  setCenter,
  setDirectionsRaw,
  setDirectionsParsed,
  directionsParsed,
  clearParsedDirections,
  lastSearch,
  type,
  setRecentSearches,
  setStepMarkers,
  onStepHover,
  onResultHover,
  hoveredIndex,
  activeStepIndex,
  activeResultIndex,
  setActiveResultIndex,
  onStepClick,
}) => {
  const getDirections = async (destination) => {
    try {
      const origin =
        localStorage.getItem("lastSearch") || "New York City, New York";

      const geocodeResponse = await axios.get("/api/directions/coordinates", {
        params: { origin, destination },
      });

      const {
        origin: originCoords,
        destination: destinationCoords,
      } = geocodeResponse.data;

      const directionsService = new window.google.maps.DirectionsService();

      directionsService.route(
        {
          origin: originCoords,
          destination: destinationCoords,
          travelMode: window.google.maps.TravelMode.DRIVING,
        },
        async (result, status) => {
          if (status === "OK") {
            setDirectionsRaw(result);

            const parsed = {
              legs: result.routes[0].legs.map((leg) => ({
                startAddress: leg.start_address,
                endAddress: leg.end_address,
                distance: leg.distance.text,
                duration: leg.duration.text,
                steps: leg.steps.map((step) =>
                  step.instructions.replace(/<[^>]*>/g, "")
                ),
              })),
            };

            setDirectionsParsed(parsed);

            const routeSteps = result.routes[0].legs[0].steps.map((step) => ({
              lat: step.start_location.lat(),
              lng: step.start_location.lng(),
            }));

            setStepMarkers(routeSteps);
            setRecentSearches(await fetchRecentSearches());

            const location = {
              origin: originCoords,
              destination: destinationCoords,
              string: {
                from: origin,
                to: destination,
              },
            };

            await saveSearchQuery(
              type,
              result.routes[0].legs,
              location,
              window.location.href,
              origin,
              destination
            );
          } else {
            console.error("❌ Google Maps routing failed:", status);
          }
        }
      );
    } catch (error) {
      console.error("❌ Error in getDirections:", error);
    }
  };

  return (
    <div className="w-full h-full p-6 bg-white rounded-2xl shadow-lg flex flex-col overflow-hidden relative">
      <h3 className="text-lg font-semibold text-gray-700 text-center mb-3">
        Search Results
      </h3>

      <AnimatePresence mode="popLayout">
        <ul className="list-none space-y-3 flex-grow overflow-y-auto">
          {Array.isArray(markers) && markers.length > 0 ? (
            markers.map((place, index) => (
              <motion.li
                key={place.place_id || index}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
                transition={{ duration: 0.3, delay: index * 0.03 }}
                className={`p-3 border-b border-gray-200 cursor-pointer flex justify-between items-center text-gray-700 rounded-lg transition-all duration-300 w-full ${
                  hoveredIndex === index || activeResultIndex === index
                    ? "bg-green-100 text-green-700 font-semibold"
                    : "hover:bg-gray-100"
                }`}
                onClick={() => {
                  setSelectedPlace(place);
                  setCenter(place.geometry?.location);
                  setActiveResultIndex(index); // ✅ Set active result
                }}
                onMouseEnter={() => onResultHover(index)}
                onMouseLeave={() => onResultHover(null)}
              >
              <div className="w-full">
                <strong>{index + 1}. {place.name}</strong>
                {place.rating && (
                  <p className="text-sm text-yellow-600 mt-1">
                    <strong>Rating:</strong> {place.rating} ⭐
                  </p>
                )}
                {place.formatted_phone_number && (
                  <p className="text-sm text-gray-600 mt-1">
                    <strong>Phone:</strong> {place.formatted_phone_number}
                  </p>
                )}
                <p className="text-sm text-gray-600">{place.vicinity}</p>
              </div>
                <button
                  className="text-green-500 text-lg hover:text-green-700 transition ml-4"
                  onClick={(e) => {
                    e.stopPropagation();
                    getDirections(place.vicinity);
                  }}
                >
                  📍
                </button>
              </motion.li>
            ))
          ) : (
            <p className="text-center text-gray-500 mt-4">
              No search results found.
            </p>
          )}
        </ul>
      </AnimatePresence>

      {directionsParsed && (
        <div className="absolute inset-0 bg-white bg-opacity-95 p-4 rounded-xl shadow-lg overflow-y-auto z-20">
          <DirectionsPanel
            directions={directionsParsed}
            onClose={clearParsedDirections}
            onStepHover={onStepHover}
            activeStepIndex={activeStepIndex}
            onStepClick={onStepClick}
          />
        </div>
      )}
    </div>
  );
};

export default ResultsList;
