import React from "react";
import { Marker, InfoWindow } from "@react-google-maps/api";

const MarkersWithInfo = ({
  markers,
  selectedPlace,
  setSelectedPlace,
  setCenter,
  map,
  hoveredIndex,
  activeResultIndex,
  setActiveResultIndex,
}) => {
  return (
    <>
      {Array.isArray(markers) &&
        markers.map((place, index) => {
          const location = place.geometry?.location;
          if (!location) return null;

          const isActive = hoveredIndex === index || activeResultIndex === index;

          return (
            <Marker
              key={place.place_id || index}
              position={location}
              label={{
                text: `${index + 1}`,
                color: "#ffffff",
                fontSize: "12px",
                fontWeight: "bold",
              }}
              icon={{
                path: window.google.maps.SymbolPath.CIRCLE,
                fillColor: isActive ? "#34D399" : "#10B981",
                fillOpacity: 1,
                strokeColor: "#065F46",
                strokeWeight: 1,
                scale: 10,
              }}
              onClick={() => {
                setSelectedPlace(place);
                setCenter(location);
                setActiveResultIndex(index); // ✅ highlight result
              }}
            />
          );
        })}

      {selectedPlace && selectedPlace.geometry?.location && (
        <InfoWindow
          position={selectedPlace.geometry.location}
          onCloseClick={() => setSelectedPlace(null)}
        >
          <div className="">
            <strong>{selectedPlace.name}</strong>
            {selectedPlace.rating && (
              <p className="text-sm text-yellow-600 mt-1">
                <strong>Rating:</strong> {selectedPlace.rating} ⭐
              </p>
            )}
            <p className="text-sm text-gray-600">{selectedPlace.vicinity}</p>
          </div>
        </InfoWindow>
      )}
    </>
  );
};

export default MarkersWithInfo;
