import React from "react";
import { OverlayView } from "@react-google-maps/api";

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

const MiniProfileCard = ({ place, onClose, onGetDirections, lastSearch, category }) => {
  const position = place.geometry?.location;
  if (!position) return null;

  // Construct photo URL using the Places Photo API
  const photoRef = place.photos?.[0]?.photo_reference;
  const photoUrl = photoRef
    ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=150&photoreference=${photoRef}&key=${GOOGLE_MAPS_API_KEY}`
    : null;

  return (
    <OverlayView
      position={position}
      mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET}
    >
      <div className="transform -translate-y-full bg-white p-3 rounded-xl shadow-md w-52 space-y-2 relative">
        <button
          onClick={onClose}
          className="absolute top-1 right-1 text-red-400 hover:text-red-600 text-sm focus:outline-none"
          title="Close"
        >
          âœ•
        </button>
        {photoUrl ? (
          <img
            src={photoUrl}
            alt={place.name}
            className="w-full h-20 object-cover rounded-md"
          />
        ) : (
          <div className="w-full h-20 bg-gray-200 animate-pulse rounded-md"></div>
        )}
        <h5 className="font-medium text-base text-gray-800 truncate">{place.name}</h5>
        <p className="text-xs text-gray-600 truncate">
          {place.vicinity || place.formatted_address}
        </p>
        {place.opening_hours?.weekday_text && (
          <ul className="text-[10px] text-gray-600 space-y-0.5 max-h-16 overflow-y-auto">
            {place.opening_hours.weekday_text.map((t, i) => (
              <li key={i}>{t}</li>
            ))}
          </ul>
        )}
        <button
          onClick={() => {
            onGetDirections(lastSearch, place.vicinity, category);
            onClose();
          }}
          className="mt-1 w-full bg-gradient-to-r from-green-400 to-blue-500 text-white text-sm py-1 rounded-lg font-medium hover:from-green-500 hover:to-blue-600 focus:outline-none"
        >
          Get Directions
        </button>
      </div>
    </OverlayView>
  );
};

export default MiniProfileCard;
